def pretty_print_metrics(metrics_data):
    # Print individual metrics
    print("Metrics:")
    for metric in metrics_data['metrics']:
        print(f"    Metric {metric[0]}:")
        for sub_metric in metric[1]:
            print(f"        Sub-metric {sub_metric[0]}: accuracy = {sub_metric[1]['accuracy']}")
    
    # Print aggregated accuracies
    print("Aggregated accuracies:")
    for aggregated_accuracy in metrics_data['Aggregated accuracy']:
        print(f"    Metric {aggregated_accuracy[0]}: accuracy = {aggregated_accuracy[1]}")

